import type { Metadata } from "next";
import { Inter } from "next/font/google";
import "./globals.css";
import { Sidebar } from "@/components/sidebar";
import { TopBar } from "@/components/top-bar";

const inter = Inter({ subsets: ["latin"] });

export const metadata: Metadata = {
  title: "Sentiment Curator",
  description: "AI-powered content curator",
};

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  return (
    <html lang="en" className="dark">
      <body className={`${inter.className} flex h-screen overflow-hidden bg-background text-foreground`}>
        <Sidebar />
        <div className="flex flex-1 flex-col overflow-hidden">
          <TopBar />
          <main className="flex-1 overflow-auto">
            {children}
          </main>
        </div>
      </body>
    </html>
  );
}

import { ConfigurationView } from "@/components/configuration-view"

export default function ConfigPage() {
    return <ConfigurationView />
}

import { DashboardView } from "@/components/dashboard-view"

export default function DashboardPage() {
    return <DashboardView />
}

"use client"

import { Home, Settings, LayoutDashboard, BarChart3 } from "lucide-react"
import { cn } from "@/lib/utils"
import Link from "next/link"
import { usePathname } from "next/navigation"

const navItems = [
  { icon: Home, label: "Home", href: "/" },
  { icon: LayoutDashboard, label: "Dashboard", href: "/dashboard" },
  { icon: BarChart3, label: "Statistics", href: "/statistics" },
  { icon: Settings, label: "Config", href: "/config" },
]

export function Sidebar() {
  const pathname = usePathname()

  return (
    <div className="flex w-16 flex-col items-center border-r border-border bg-sidebar py-4">
      <div className="mb-8 flex h-10 w-10 items-center justify-center rounded-lg bg-primary text-primary-foreground font-mono font-semibold text-sm">
        SC
      </div>
      <nav className="flex flex-1 flex-col gap-2">
        {navItems.map((item) => {
          const isActive = pathname === item.href || (item.href !== "/" && pathname.startsWith(item.href))
          return (
            <Link
              key={item.href}
              href={item.href}
              className={cn(
                "flex h-12 w-12 items-center justify-center rounded-lg transition-colors",
                isActive
                  ? "bg-sidebar-accent text-sidebar-accent-foreground"
                  : "text-sidebar-muted-foreground hover:bg-sidebar-accent/50 hover:text-sidebar-accent-foreground",
              )}
              title={item.label}
            >
              <item.icon className="h-5 w-5" />
            </Link>
          )
        })}
      </nav>
    </div>
  )
}

"use client"

import { Button } from "@/components/ui/button"
import { ChevronRight, Play } from "lucide-react"
import { triggerCrawler } from "@/lib/api"
import { usePathname } from "next/navigation"

export function TopBar() {
  const pathname = usePathname()

  const handleRunCrawler = async () => {
    try {
      // Use default or configured values. For now, hardcoded or fetched from config?
      // Ideally, trigger without args and let backend use stored config.
      // But backend endpoint expects urls and prompt.
      // Let's assume we send empty to use defaults or we need to fetch config first.
      // For simplicity, let's just trigger with a dummy or empty list and let backend handle it if possible.
      // Or better, update backend to allow running with stored config.
      // Current backend endpoint: POST /crawler/run { urls: [], prompt: "" }

      // Let's just alert for now as we don't have the config here easily without fetching.
      // Or we can fetch config then trigger.
      alert("Triggering crawler...")
      await triggerCrawler(["https://news.ycombinator.com"], "Find AI news")
      alert("Crawler started!")
    } catch (error) {
      console.error("Failed to run crawler:", error)
      alert("Failed to run crawler.")
    }
  }

  // Generate breadcrumbs based on pathname
  const segments = pathname.split("/").filter(Boolean)

  return (
    <div className="flex h-12 items-center justify-between border-b border-border bg-card px-4 shrink-0">
      <div className="flex items-center gap-2 text-sm text-muted-foreground">
        <span className="text-foreground font-medium">{"Sentiment Curator"}</span>
        {segments.map((segment, index) => (
          <div key={segment} className="flex items-center gap-2">
            <ChevronRight className="h-4 w-4" />
            <span className="capitalize">{segment}</span>
          </div>
        ))}
      </div>
      <Button size="sm" className="gap-2" onClick={handleRunCrawler}>
        <Play className="h-4 w-4" />
        {"Run Crawler"}
      </Button>
    </div>
  )
}

"use client"

import { useState, useEffect } from "react"
import { PostList, Post } from "@/components/post-list"
import { PostDetail, PostDetailData } from "@/components/post-detail"
import { fetchPosts } from "@/lib/api"

export function DashboardView() {
    const [posts, setPosts] = useState<Post[]>([])
    const [selectedPostId, setSelectedPostId] = useState<string | null>(null)
    const [loading, setLoading] = useState(true)

    useEffect(() => {
        async function loadPosts() {
            try {
                const data = await fetchPosts()
                // Map backend data to frontend interface
                const mappedPosts: Post[] = data.map((p: any) => ({
                    id: p.id.toString(),
                    title: p.title || "Untitled",
                    timestamp: new Date(p.created_at).toLocaleString(),
                    type: "article", // Default or infer
                    date: p.created_at,
                    reactions: p.feedback_score || 0,
                    threadCount: p.thread_count || 0,
                    summary: p.summary,
                    content: p.content,
                    url: p.url,
                    sentiment: "neutral", // Default
                    // Map reaction_data if available
                    reactionData: p.reaction_data
                }))
                setPosts(mappedPosts)
                if (mappedPosts.length > 0) {
                    setSelectedPostId(mappedPosts[0].id)
                }
            } catch (error) {
                console.error("Failed to fetch posts:", error)
            } finally {
                setLoading(false)
            }
        }
        loadPosts()
    }, [])

    const selectedPost = posts.find((p) => p.id === selectedPostId)

    // Map selectedPost to PostDetailData
    const postDetailData: PostDetailData | null = selectedPost
        ? {
            id: selectedPost.id,
            title: selectedPost.title,
            url: selectedPost.url || "",
            timestamp: selectedPost.timestamp,
            content: selectedPost.content || "",
            summary: selectedPost.summary || "",
            sentiment: selectedPost.sentiment || "neutral",
            reactions: [], // TODO: Parse reactionData
            threads: [], // TODO: Fetch threads if separate
        }
        : null

    return (
        <div className="flex h-full">
            <PostList
                posts={posts}
                selectedPost={selectedPostId}
                onSelectPost={setSelectedPostId}
            />
            <PostDetail post={postDetailData} />
        </div>
    )
}

"use client"

import { cn } from "@/lib/utils"

import { Badge } from "@/components/ui/badge"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { ScrollArea } from "@/components/ui/scroll-area"
import { Separator } from "@/components/ui/separator"
import { ExternalLink, MessageCircle } from "lucide-react"

// Define interfaces based on what was in mockPostData
interface Reaction {
  emoji: string
  count: number
  label: string
}

interface Thread {
  id: string
  author: string
  timestamp: string
  content: string
  reactions: number
}

export interface PostDetailData {
  id: string
  title: string
  url: string
  timestamp: string
  content: string
  summary: string
  sentiment: "positive" | "neutral" | "negative"
  reactions: Reaction[]
  threads: Thread[]
}

interface PostDetailProps {
  post: PostDetailData | null
}

export function PostDetail({ post }: PostDetailProps) {
  if (!post) {
    return (
      <div className="flex-1 flex items-center justify-center bg-background text-muted-foreground">
        Select a post to view details
      </div>
    )
  }

  const sentimentColors = {
    positive: "bg-chart-3 text-chart-3",
    neutral: "bg-chart-2 text-chart-2",
    negative: "bg-destructive text-destructive",
  }

  return (
    <div className="flex-1 bg-background">
      <ScrollArea className="h-[calc(100vh-3rem)]">
        <div className="mx-auto max-w-4xl p-6">
          <div className="mb-4 flex items-start justify-between">
            <div className="flex-1">
              <h1 className="mb-2 text-2xl font-bold text-foreground">{post.title}</h1>
              <div className="flex items-center gap-3 text-sm text-muted-foreground">
                <span className="font-mono">{post.timestamp}</span>
                <Separator orientation="vertical" className="h-4" />
                <a
                  href={post.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="flex items-center gap-1 text-primary hover:underline"
                >
                  {"View Source"}
                  <ExternalLink className="h-3 w-3" />
                </a>
              </div>
            </div>
            <Badge
              variant="outline"
              className={cn("border-0 bg-opacity-10 font-medium", sentimentColors[post.sentiment])}
            >
              {post.sentiment.toUpperCase()}
            </Badge>
          </div>

          <Card className="mb-6">
            <CardHeader>
              <CardTitle className="text-base">{"Summary"}</CardTitle>
            </CardHeader>
            <CardContent>
              <p className="text-sm leading-relaxed text-muted-foreground">{post.summary}</p>
            </CardContent>
          </Card>

          <Card className="mb-6">
            <CardHeader>
              <CardTitle className="text-base">{"Emoji Reactions"}</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-2 gap-4 md:grid-cols-4">
                {post.reactions.map((reaction, index) => (
                  <div
                    key={index}
                    className="flex items-center gap-3 rounded-lg border border-border bg-card p-3 transition-colors hover:bg-accent"
                  >
                    <span className="text-2xl">{reaction.emoji}</span>
                    <div className="flex-1">
                      <div className="text-lg font-semibold text-foreground">{reaction.count}</div>
                      <div className="text-xs text-muted-foreground">{reaction.label}</div>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>

          {post.threads && post.threads.length > 0 && (
            <Card className="mb-6">
              <CardHeader>
                <CardTitle className="flex items-center gap-2 text-base">
                  <MessageCircle className="h-4 w-4" />
                  {"Thread Discussions"}
                  <Badge variant="secondary" className="ml-auto">
                    {post.threads.length}
                  </Badge>
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                {post.threads.map((thread) => (
                  <div key={thread.id} className="rounded-lg border border-border bg-card/50 p-4">
                    <div className="mb-2 flex items-center justify-between">
                      <div className="flex items-center gap-2">
                        <span className="font-mono text-sm font-medium text-primary">{thread.author}</span>
                        <span className="text-xs text-muted-foreground">{thread.timestamp}</span>
                      </div>
                      <div className="flex items-center gap-1 rounded-full bg-accent px-2 py-1">
                        <span className="text-xs text-muted-foreground">ğŸ‘</span>
                        <span className="text-xs font-medium text-foreground">{thread.reactions}</span>
                      </div>
                    </div>
                    <p className="text-sm leading-relaxed text-foreground">{thread.content}</p>
                  </div>
                ))}
              </CardContent>
            </Card>
          )}

          <Card>
            <CardHeader>
              <CardTitle className="text-base">{"Content"}</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="prose prose-invert max-w-none text-foreground">
                {post.content.split("\n").map((line, index) => {
                  if (line.startsWith("# ")) {
                    return (
                      <h1 key={index} className="mb-4 mt-6 text-2xl font-bold text-foreground">
                        {line.substring(2)}
                      </h1>
                    )
                  }
                  if (line.startsWith("## ")) {
                    return (
                      <h2 key={index} className="mb-3 mt-5 text-xl font-semibold text-foreground">
                        {line.substring(3)}
                      </h2>
                    )
                  }
                  if (line.startsWith("**") && line.endsWith("**")) {
                    return (
                      <p key={index} className="mb-2 font-semibold text-foreground">
                        {line.substring(2, line.length - 2)}
                      </p>
                    )
                  }
                  if (line.startsWith("> ")) {
                    return (
                      <blockquote
                        key={index}
                        className="my-4 border-l-4 border-primary pl-4 italic text-muted-foreground"
                      >
                        {line.substring(2)}
                      </blockquote>
                    )
                  }
                  if (line.match(/^\d+\./)) {
                    return (
                      <li key={index} className="mb-1 ml-4 text-foreground">
                        {line.substring(line.indexOf(".") + 2)}
                      </li>
                    )
                  }
                  if (line.startsWith("- ")) {
                    return (
                      <li key={index} className="mb-1 ml-4 text-foreground">
                        {line.substring(2)}
                      </li>
                    )
                  }
                  if (line.trim() === "") {
                    return <div key={index} className="h-2" />
                  }
                  return (
                    <p key={index} className="mb-3 leading-relaxed text-foreground">
                      {line}
                    </p>
                  )
                })}
              </div>
            </CardContent>
          </Card>
        </div>
      </ScrollArea>
    </div>
  )
}

