import { Component, ChangeDetectionStrategy, OnInit } from "@angular/core";
import { Router, RouterOutlet } from "@angular/router";
import { CustomizeJsonLoader } from "@app/bro-packages/automate-item-parts/customize/customize-json-loader";
import { SCENE_MOVE_OPTION } from "@app/services/engine";
import { ContextMenuActionService } from "./_lib/context-menu-action.service";
import { CustomizeNotiMarkerService } from "./_lib/customize-noti-marker.service";

@Component({
    standalone: true,
    imports: [RouterOutlet],
    template: `
        <section class="pubg-contents lobbyBg-transparent">
            <div>
                <router-outlet></router-outlet>
            </div>
        </section>
    `,
    styleUrls: ["./customize.common.scss"],
    providers: [ContextMenuActionService, CustomizeNotiMarkerService],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class CustomizeConsoleComponent implements OnInit {
    constructor(
        private router: Router,
        private customizeJsonLoader: CustomizeJsonLoader
    ) {}

    ngOnInit(): void {
        const partDescId: string = history.state.partDescId;
        const itemDescId: string = history.state.itemDescId;
        if (partDescId && itemDescId) {
            const customizeUrl = this.getUrlByPartDescId(partDescId);
            const category = this.getCategoryByPartDescId(partDescId);
            this.router.navigate([`/game/customize2/${customizeUrl}`], {
                queryParams: { scenemove: SCENE_MOVE_OPTION.FADE_IN_OUT },
                state: { category, itemDescId },
            });
        } else {
            this.router.navigate(["/game/customize2/character"]);
        }
    }

    private getCategoryByPartDescId(partDescId: string): string {
        const [_, partDesc] = partDescId.split(".");
        const parts = this.customizeJsonLoader.loadPartsJson();
        const category = parts[partDesc].customize.category;
        return category;
    }

    private getUrlByPartDescId(partDescId: string): string {
        const [_, partDesc] = partDescId.split(".");
        const parts = this.customizeJsonLoader.loadPartsJson();
        const { lnb, tab } = parts[partDesc].customize;
        if (tab) {
            return `${lnb}/${tab}`;
        }
        return lnb;
    }
}

