import { Injectable } from "@angular/core";
import { SCENE_NAME, SCENE_NAME_V2 } from "../engine/types/scene-name.enum";
import { LobbyCustomizingData } from "@app/_lib/Bro/unrealApi/unreal-apis";
import { CharacterCustomizationOptions } from "@app/_lib/Bro/RpcApis";
import { CAMERA_TYPE, LobbyItemSequenceTag, SCENE_MOVE_OPTION, VIEW_MODE_CATEGORY } from "../engine";
import {
    bikePreviewParameter,
    carPreviewParameter,
    defaultPreviewParameter,
    handgunPreviewParameter,
    meleePreviewParameter,
    PreviewParameter,
    smgPreviewParameter,
    weaponPreviewParameter,
    parachutePreviewParameter,
    brdmPreviewParameter,
    aquarailPreviewParameter,
    gliderPreviewParameter,
    energyDrinkPreviewParameter,
    smokePreviewParameter,
} from "@app/constants/partdesc.preview-constant";
import {
    aquarailPassPreviewParameter,
    bikePassPreviewParameter,
    brdmPassPreviewParameter,
    carPassPreviewParameter,
    defaultWeaponPassPreviewParameter,
    gliderPassPreviewParameter,
    handgunPassPreviewParameter,
    m79PassPreviewParameter,
    smokeBombPassPreviewParameter,
} from "@app/constants/partdesc.pass-preview-constant";

type SceneMoveOption = "Teleport" | "CameraMove" | "FadeInOut";
declare let engine: any;

@Injectable({
    providedIn: "root",
})
export class SceneChangeService {
    private _curSceneName: SCENE_NAME | SCENE_NAME_V2 | string;

    public get curSceneName(): SCENE_NAME | SCENE_NAME_V2 | string {
        return this._curSceneName;
    }

    constructor() {}

    public changeScene(sceneName: string, sceneMoveOption: SceneMoveOption): void {
        this._curSceneName = sceneName;
        window.FEToUnrealApi.changeSceneFromNameWithSceneTravel(sceneName, sceneMoveOption);
    }

    public changeSceneWithLobbyCustom(
        sceneName: string,
        lobbyCustomizingDataList: LobbyCustomizingData[],
        sceneMoveOption: SceneMoveOption
    ): void {
        this._curSceneName = sceneName;
        window.FEToUnrealApi.changeSceneWithLobbyCustomizing(sceneName, lobbyCustomizingDataList, sceneMoveOption);
    }

    public showCharacterViewMode(
        sceneName: SCENE_NAME | SCENE_NAME_V2 | string,
        options: Pick<CharacterCustomizationOptions, "ItemIds" | "StringOptions">,
        cameraType: CAMERA_TYPE = CAMERA_TYPE.WHOLE_BODY,
        sceneMoveOption: SCENE_MOVE_OPTION = SCENE_MOVE_OPTION.TELEPORT
    ): void {
        this._curSceneName = sceneName;
        engine.trigger(
            "ShowCharacterViewModeExWithSceneTravel",
            sceneName,
            {
                BoolOptions: [],
                FloatOptions: [],
                StringOptions: options.StringOptions,
                ItemIds: options.ItemIds,
                ActionSlots: "",
                IngameEquipableItemIds: [],
                VehicleSkinInfos: [],
            },
            cameraType,
            sceneMoveOption
        );
    }

    /**
     * @param sceneName ë³´ì—¬ì£¼ê³ ì í•˜ëŠ” ì”¬ ë„¤ì„
     * @param category ë³´ì´ê³ ì í•˜ëŠ” ì•„ì´í…œ íƒ€ì…
     * @param baseGameItemId ItemDataì— í¬í•¨ëœ ì •ë³´
     * @param gameItemId ItemDataì— í¬í•¨ëœ ì •ë³´
     * @param partDescId ItemDataì— í¬í•¨ëœ ì •ë³´
     * @param sceneMoveOption ì”¬ ì´ë™ ë°©ì‹ ì„¤ì •
     * @param vehicleCustomParts ì»¤ìŠ¤í…€ ê°€ëŠ¥í•œ ì°¨ëŸ‰ìŠ¤í‚¨ì˜ ê²½ìš° ì»¤ìŠ¤í…€í•  íŒŒì¸  ì •ë³´ë“¤ì„ ì–´ë ˆì´ë¡œ ë‚´ë ¤ì¤€ë‹¤(GameItemId)
     * @param lobbyItemSequence ë¡œë¹„ ì»¤ìŠ¤í„°ë§ˆì´ì§•ì— í•„ìš”í•œ ìƒíƒœ ì •ë³´(ì°¨ëŸ‰ ì „ì‹œ ë ˆë”” / ì–¸ë ˆë”” / ê¸°ë³¸ê°’)
     */
    public showItemViewMode(
        sceneName: SCENE_NAME | SCENE_NAME_V2 | string,
        category: VIEW_MODE_CATEGORY,
        baseGameItemId: string,
        gameItemId: string,
        partDescId: string,
        sceneMoveOption: SCENE_MOVE_OPTION = SCENE_MOVE_OPTION.TELEPORT,
        vehicleCustomParts: string[] = [],
        lobbyItemSequence: LobbyItemSequenceTag = ""
    ): void {
        const { InitialScale, ZoomMultiplier, ViewModeScaleMin, ViewModeScaleMax } = this.getPreviewParameter(
            partDescId,
            sceneName
        );
        this._curSceneName = sceneName;
        engine.trigger(
            "ShowItemViewModeWithSceneTravel",
            sceneName,
            category,
            {
                TargetItemId: baseGameItemId,
                SkinItemId: gameItemId,
                PartIds: vehicleCustomParts,
                SequenceTag: lobbyItemSequence,
                InitialScale,
                ZoomMultiplier,
                ViewModeScaleMin,
                ViewModeScaleMax,
            },
            sceneMoveOption
        );
    }

    private getPreviewParameter(partDescId: string, sceneName: SCENE_NAME | SCENE_NAME_V2 | string): PreviewParameter {
        if (!this.isPreviewParameterAppliedScene(sceneName)) {
            return defaultPreviewParameter;
        }

        if (
            sceneName === SCENE_NAME_V2.PASS_CENTER_PREVIEW_PARACHUTE ||
            sceneName === SCENE_NAME_V2.PASS_CENTER_PREVIEW_VEHICLE ||
            sceneName === SCENE_NAME_V2.PASS_CENTER_PREVIEW_WEAPON
        ) {
            if (partDescId.isVehiclePartDesc()) {
                if (partDescId.isBrdmDesc()) {
                    return brdmPassPreviewParameter;
                } else if (partDescId.isBikePartDesc()) {
                    return bikePassPreviewParameter;
                } else if (partDescId.isCarPartDesc()) {
                    return carPassPreviewParameter;
                } else if (partDescId.isAquarailPartDesc()) {
                    return aquarailPassPreviewParameter;
                } else if (partDescId.isGliderPartDesc()) {
                    return gliderPassPreviewParameter;
                }
                return defaultPreviewParameter;
            } else if (partDescId.isWeaponPartDesc()) {
                if (partDescId === "partdesc.m79") {
                    return m79PassPreviewParameter;
                }
                if (partDescId.isHandgunPartDesc()) {
                    return handgunPassPreviewParameter;
                }
                if (partDescId.isSmokePartDesc()) {
                    return smokeBombPassPreviewParameter;
                }
                return defaultWeaponPassPreviewParameter;
            }
            return defaultPreviewParameter;
        }

        if (partDescId.isWeaponPartDesc()) {
            if (partDescId.isHandgunPartDesc()) {
                return handgunPreviewParameter;
            } else if (partDescId.isMeleePartDesc()) {
                return meleePreviewParameter;
            } else if (partDescId.isSMGPartDesc()) {
                return smgPreviewParameter;
            } else if (partDescId.isEnergyDrinkPartDesc()) {
                return energyDrinkPreviewParameter;
            } else if (partDescId.isSmokePartDesc()) {
                return smokePreviewParameter;
            }
            return weaponPreviewParameter;
        } else if (partDescId.isVehiclePartDesc()) {
            if (partDescId.isBrdmDesc()) {
                return brdmPreviewParameter;
            } else if (partDescId.isBikePartDesc()) {
                return bikePreviewParameter;
            } else if (partDescId.isCarPartDesc()) {
                return carPreviewParameter;
            } else if (partDescId.isAquarailPartDesc()) {
                return aquarailPreviewParameter;
            } else if (partDescId.isGliderPartDesc()) {
                return gliderPreviewParameter;
            }
        } else if (partDescId.isParachutePartDesc()) {
            return parachutePreviewParameter;
        }
        return defaultPreviewParameter;
    }

    private isPreviewParameterAppliedScene(sceneName: SCENE_NAME | SCENE_NAME_V2 | string): boolean {
        if (
            sceneName === SCENE_NAME.PREVIEW_ITEM ||
            sceneName === SCENE_NAME.PREVIEW_SET_ITEM ||
            sceneName === SCENE_NAME.PREVIEW_ITEM_GROUND ||
            sceneName === SCENE_NAME.PREVIEW_SET_ITEM_GROUND ||
            sceneName === SCENE_NAME.CUSTOMIZE_VEHICLE ||
            sceneName === SCENE_NAME_V2.CUSTOMIZE_EDIT_VEHICLE ||
            sceneName === SCENE_NAME_V2.PASS_CENTER_PREVIEW_PARACHUTE ||
            sceneName === SCENE_NAME_V2.PASS_CENTER_PREVIEW_VEHICLE ||
            sceneName === SCENE_NAME_V2.PASS_CENTER_PREVIEW_WEAPON
        ) {
            return true;
        }
        return false;
    }
}

