import { Router } from "@angular/router";

import { ItemData } from "@app/_lib/Bro/RpcApis";
import { isItemData } from "@app/_lib/utils/type-guard-util";
import { SCENE_MOVE_OPTION } from "@app/services/engine";

import { ClaimableRewardType, RewardCaseFactoryFunction, SingleReceivedRewardCase } from "../index";

class BaseCustomizeItem implements SingleReceivedRewardCase {
    constructor(public reward: ItemData) {}

    description: string = "STORE:MODAL_BODY_4";

    async onClickMove(router: Router): Promise<void> {
        if (router.url.includes("customize") && !router.url.includes("outer")) {
            const [_, partDesc] = this.reward.PartDescId.split(".");
            const customizeUrl = await this.getUrlByPartDescId(partDesc);
            const category = await this.getCategoryByPartDescId(partDesc);
            router.navigate(
                [`/`, { outlets: { primary: `game/customize2/${customizeUrl}`, outer: null, outer2: null } }],
                {
                    queryParams: { scenemove: SCENE_MOVE_OPTION.FADE_IN_OUT },
                    state: { category, itemDescId: this.reward.ItemDescId },
                }
            );
        } else {
            router.navigate(["/", { outlets: { primary: "game/customize2", outer: null, outer2: null } }], {
                queryParams: { scenemove: SCENE_MOVE_OPTION.FADE_IN_OUT },
                state: { itemDescId: this.reward.ItemDescId, partDescId: this.reward.PartDescId },
            });
        }
    }

    private async getUrlByPartDescId(partDesc: string): Promise<string> {
        const parts = await this.loadPartsJson();
        const { lnb, tab } = parts[partDesc].customize;
        return tab ? `${lnb}/${tab}` : lnb;
    }

    private async getCategoryByPartDescId(partDesc: string): Promise<string> {
        const parts = await this.loadPartsJson();
        return parts[partDesc].customize.category;
    }

    private async loadPartsJson(): Promise<any> {
        const response = await fetch("app/_lib/Bro/game-design-data/item-category/Parts.json");
        const json = await response.json();
        return json;
    }
}

export const CustomizeRewardFactory: RewardCaseFactoryFunction = (
    reward: ClaimableRewardType
): SingleReceivedRewardCase => {
    if (!isItemData(reward)) {
        return null;
    }

    const nonMarketableList = ["itemdesc.15100005", "itemdesc.15100006", "itemdesc.15100007", "itemdesc.15100008"];
    if (!!nonMarketableList.find((itemDescId) => itemDescId === reward.ItemDescId)) {
        return new BaseCustomizeItem(reward);
    }

    /**
     * ì»¤ìŠ¤í„°ë§ˆì´ì¦ˆ ë‚´ ê°€ì¥ ë²”ìœ„ê°€ ë„“ì€ ì¡°ê±´ë¬¸ì´ê¸° ë•Œë¬¸ì—, ìœ„ ì¡°ê±´ë¬¸ ì•„ë˜ì— ì½”ë“œë¥¼ ì‘ì„± í•  ê²½ìš° ì˜ë„ì¹˜ ì•ŠëŠ” ë™ì‘ì´ ë°œìƒ í•  ìˆ˜ ìˆìŠµë‹ˆë‹¤.
     * ì´ëŸ¬í•œ ì´ìœ ë¡œ ìœ„ ì¡°ê±´ë¬¸ ì•„ë˜ì—ëŠ” ì½”ë“œë¥¼ ì‘ì„±í•˜ì§€ ë§ˆì‹œê¸° ë°”ëë‹ˆë‹¤.
     */
    if (
        reward.PartDescId.isWardrobePartDesc() ||
        reward.PartDescId.isAppearancePartDesc() ||
        reward.PartDescId.isWeaponPartDesc() ||
        reward.PartDescId.isCharmPartDesc() ||
        reward.PartDescId.isEmotePartDesc() ||
        reward.PartDescId.isSprayPartDesc() ||
        reward.PartDescId.isVehiclePartDesc() ||
        reward.PartDescId.isLobbyPartDesc() ||
        reward.PartDescId.isUtilityPartDesc() ||
        reward.PartDescId.isLobbyPosePartDesc()
    ) {
        return new BaseCustomizeItem(reward);
    }

    return null;
};

