import { Component, OnInit, ChangeDetectionStrategy, OnDestroy } from "@angular/core";
import { PUBGStore } from "@app/ngrx-store/pubg-store";
import { IAppState } from "@app/reduxes/app-states";
import { Subject } from "rxjs";
import { distinctUntilChanged, takeUntil } from "rxjs/operators";
import { CharacterEngineService } from "./services/character-engine.service";
import * as uiActions from "@app/redux/actions/ui.actions";
import * as inventoryActions from "@app/redux/actions/inventory.actions";
import { CharacterPresetService } from "./services/character-preset.service";
import { CharacterPresetFrogService } from "@app/services/frog";
import { WardrobeItemFocusStore } from "./wardrobe2/_lib/wardrobe-item-focus.store";
import { CharacterItemSelectStore } from "./stores/character-item-select.store";
import { GearFilterStore } from "./wardrobe2/_lib/gear-filter.store";
import { CharacterItemHoverStore } from "./stores/character-item-hover.store";
import { CharacterSaveChangeService } from "./services/character-save-change.service";
import { CharacterItemFocusStore } from "./stores/character-item-focus.store";
import { CharacterSkinItemHoverStore } from "./stores/character-skin-item-hover.store";
import { selectAppearance } from "@app/redux/selectors/skin.selector";
import { AppearanceFittingRoomService } from "./services/fitting-room.service/appearance-fitting-room.service";
import { GearFittingRoomService } from "./services/fitting-room.service/gear-fitting-room.service";
import { LobbyPoseFittingRoomService } from "./services/fitting-room.service/lobby-pose-fitting-room.service";
import { WardrobeFittingRoomService } from "./services/fitting-room.service/wardrobe-fitting-room.service";
import { Actions, ofType } from "@ngrx/effects";
import { WardrobeItemExploreStore } from "./stores/wardrobe-item-explore.store";
import { CharacterMenuStore } from "@app/bro-packages/automate-item-parts/customize/character/character-menu.store";
import { RouterOutlet } from "@angular/router";
import { CharacterTabPcComponent } from "./components/character-tab/character-tab.pc";
import { CharacterPresetPaginationCommonComponent } from "./components/character-preset-pagination/character-preset-pagination.common";

@Component({
    standalone: true,
    imports: [RouterOutlet, CharacterTabPcComponent, CharacterPresetPaginationCommonComponent],
    templateUrl: "./character.common.html",
    styleUrls: ["./character.common.scss"],
    changeDetection: ChangeDetectionStrategy.OnPush,
    providers: [
        // service
        AppearanceFittingRoomService,
        GearFittingRoomService,
        LobbyPoseFittingRoomService,
        WardrobeFittingRoomService,
        CharacterEngineService,
        CharacterPresetService,
        CharacterPresetFrogService,
        CharacterSaveChangeService,
        // store
        WardrobeItemFocusStore,
        CharacterItemSelectStore,
        CharacterItemHoverStore,
        CharacterSkinItemHoverStore,
        CharacterItemFocusStore,
        WardrobeItemExploreStore,
        GearFilterStore,
        CharacterMenuStore,
    ],
})
export class CharacterPcComponent implements OnInit, OnDestroy {
    private readonly onDestroy$ = new Subject<void>();

    constructor(
        private actions$: Actions,
        private store: PUBGStore<IAppState>,
        private characterEngineService: CharacterEngineService,
        private characterSaveChangeService: CharacterSaveChangeService,
        private wardrobeFittinRoomService: WardrobeFittingRoomService,
        private gearFittingRoomService: GearFittingRoomService,
        private lobbyPoseFittingRoomService: LobbyPoseFittingRoomService
    ) {}

    ngOnInit(): void {
        const appearance = this.store.snapshot(selectAppearance);
        this.characterEngineService.createLobbyCharacter(appearance.gender);

        this.detectHasChange();
        this.detectExchangeBpSuccess();
    }

    ngOnDestroy(): void {
        this.onDestroy$.next();
        this.onDestroy$.complete();

        this.store.dispatch(uiActions.setHasCharacterUnsavedChanges({ hasCharacterUnsavedChanges: false }));
    }

    private detectHasChange(): void {
        this.characterSaveChangeService.hasChange$
            .pipe(distinctUntilChanged(), takeUntil(this.onDestroy$))
            .subscribe((hasCharacterUnsavedChanges) => {
                this.store.dispatch(uiActions.setHasCharacterUnsavedChanges({ hasCharacterUnsavedChanges }));
            });
    }

    private detectExchangeBpSuccess(): void {
        this.actions$
            .pipe(ofType(inventoryActions.sellItemForBPSuccess), takeUntil(this.onDestroy$))
            .subscribe((action) => {
                const partDescId = action.item.PartDescId;
                if (partDescId.isGearPartDesc()) {
                    this.gearFittingRoomService.exchangeBp(action.item);
                    return;
                }
                if (partDescId.isWardrobePartDesc()) {
                    this.wardrobeFittinRoomService.exchangeBp(action.item);
                    return;
                }
                if (partDescId.isLobbyPosePartDesc()) {
                    this.lobbyPoseFittingRoomService.exchangeBp(action.item);
                    return;
                }
            });
    }
}

