        this.onDestroy$.next();
        this.onDestroy$.complete();

        this.store.dispatch(uiActions.setHasCharacterUnsavedChanges({ hasCharacterUnsavedChanges: false }));
    }

    private detectHasChange(): void {
        this.characterSaveChangeService.hasChange$
            .pipe(distinctUntilChanged(), takeUntil(this.onDestroy$))
            .subscribe((hasCharacterUnsavedChanges) => {
                this.store.dispatch(uiActions.setHasCharacterUnsavedChanges({ hasCharacterUnsavedChanges }));
            });
    }

    private detectExchangeBpSuccess(): void {
        this.actions$
            .pipe(ofType(inventoryActions.sellItemForBPSuccess), takeUntil(this.onDestroy$))
            .subscribe((action) => {
                const partDescId = action.item.PartDescId;
                if (partDescId.isGearPartDesc()) {
                    this.gearFittingRoomService.exchangeBp(action.item);
                    return;
                }
                if (partDescId.isWardrobePartDesc()) {
                    this.wardrobeFittinRoomService.exchangeBp(action.item);
                    return;
                }
                if (partDescId.isLobbyPosePartDesc()) {
                    this.lobbyPoseFittingRoomService.exchangeBp(action.item);
                    return;
                }
            });
    }
}

