import { Injectable, OnDestroy } from "@angular/core";
import { EngineService, SCENE_MOVE_OPTION, SCENE_NAME_V2 } from "@app/services/engine";
import { ItemData, LobbyPoseLocationType, OriginType } from "@app/_lib/Bro/RpcApis";
import { Appearance } from "@app/models/appearance";
import { ProgressiveGameItemIdService } from "@app/services/progressive-gameitemid";
import { SceneChangeService } from "@app/services/scene-change/scene-change.service";

@Injectable()
export class CharacterEngineService implements OnDestroy {
    constructor(
        private progressiveGameItemId: ProgressiveGameItemIdService,
        private engine: EngineService,
        private sceneService: SceneChangeService
    ) {}

    ngOnDestroy(): void {
        this.engine.destroyLobbyCharacter(0);
    }

    public createLobbyCharacter(gender: string): void {
        if (gender) {
            this.engine.createLobbyCharacter({
                IsGenderMale: gender === "male",
                NetId: "",
                NickName: "",
                PlatformProvider: OriginType.Unknown,
                SlotIndex: 0,
                bCreateWithVisible: false,
            });
        }
    }

    public updateLobbyCharacter(params: {
        wardrobes: ItemData[];
        appearance: Appearance;
        gears?: ItemData[];
        contender?: ItemData;
    }) {
        const gears = params?.gears ?? [];
        const contenderGameItemId = this.progressiveGameItemId.getContenderGameItemId(params.contender?.ItemDescId);
        const itemIds = this.getItemIds(...params.wardrobes, ...gears);
        return this.engine.updateLobbyCharacter(0, {
            ItemIds: [...itemIds, contenderGameItemId],
            StringOptions: params.appearance.toStringOptions(),
        });
    }

    private getItemIds(...itemDataList: ItemData[]): string[] {
        return itemDataList
            .filter((item) => !!item)
            .flatMap((item) => [item.GameItemId, item.BaseGameItemId].filter((id) => !!id));
    }

    public updateLobbyPose(item: ItemData): void {
        this.engine.updateLobbyPose(item, LobbyPoseLocationType.Normal);
    }

    public resetLobbyPose(): void {
        this.engine.resetLobbyPose();
    }

    public changeSceneFromName(sceneName: SCENE_NAME_V2, sceneMoveOption = SCENE_MOVE_OPTION.FADE_IN_OUT) {
        this.sceneService.changeScene(sceneName, sceneMoveOption);
    }

    showCharacter(): void {
        this.engine.cancelOverrideCharacterVisibility(0);
    }

    hideCharacter(): void {
        this.engine.setOverrideCharacterVisibility(0, false);
    }
}

