ï»¿using System.Threading;

namespace Bro.Services.Test.ABTest
{
    public class ABTestTest : BaseServiceUnitTest
    {
        [Fact]
        public async Task GetABTestGroup_should_return_fail_if_test_id_is_not_valid()
        {
            OverrideDescs(OriginType.Bro, GetABTestDescs().ToArray());
            var service = CreateService<ABTestService.ABTestService>();

            var dateTime = Substitute.For<IBroDateTime>();
            dateTime.UtcNow.ReturnsForAnyArgs(DateTime.Parse("2023-11-20 12:00:00"));
            BroDateTimeStatic.OpenTestScope(dateTime);

            var result = await service.GetABTestGroupAsync("account.a", "test.4");

            Assert.True(result.IsError());
        }

        [Fact]
        public async Task GetABTestGroup_should_return_default_if_test_is_not_in_progress()
        {
            OverrideDescs(OriginType.Bro, GetABTestDescs().ToArray());
            var service = CreateService<ABTestService.ABTestService>();

            var dateTime = Substitute.For<IBroDateTime>();
            dateTime.UtcNow.ReturnsForAnyArgs(DateTime.Parse("2023-11-20 12:00:00"));
            BroDateTimeStatic.OpenTestScope(dateTime);

            var result = await service.GetABTestGroupAsync("account.a", "test.1");

            Assert.True(result.IsSuccess());
            Assert.Equal(ABTestGroup.A, result.Result);
        }

        [Fact]
        public async Task GetABTestGroup_should_return_user_test_group()
        {
            OverrideDescs(OriginType.Bro, GetABTestDescs().ToArray());
            var service = CreateService<ABTestService.ABTestService>();

            _gear.MockRpc(NameOf.AccountApi());
            _gear.AccountABTestApiProxy().GetABTestGroupAsync(default, default, default)
                .ReturnsForAnyArgs(HcTypedResult<ABTestGroup>.Success(ABTestGroup.C));

            var dateTime = Substitute.For<IBroDateTime>();
            dateTime.UtcNow.ReturnsForAnyArgs(DateTime.Parse("2099-12-31 16:00:00"));
            BroDateTimeStatic.OpenTestScope(dateTime);

            var result = await service.GetABTestGroupAsync("account.a", "test.1");

            Assert.True(result.IsSuccess());
            Assert.Equal(ABTestGroup.C, result.Result);
        }

        [Fact]
        public async Task SetABTestGroup_should_set_test_group_properly()
        {
            OverrideDescs(OriginType.Bro, GetABTestDescs().ToArray());
            var service = CreateService<ABTestService.ABTestService>();

            _gear.MockRpc(NameOf.AccountApi());
            _gear.AccountABTestApiProxy().GetABTestGroupsAsync(default, default)
                .ReturnsForAnyArgs(HcTypedResult<List<ABTestGroupAssignment>>.Success(GetABTestGroupAssignments()));

            var dateTime = Substitute.For<IBroDateTime>();
            dateTime.UtcNow.ReturnsForAnyArgs(DateTime.Parse("2099-12-30 11:00:00"));
            BroDateTimeStatic.OpenTestScope(dateTime);

            await service.SetABTestGroupsOnLoginAsync("account.a");

            await _gear.AccountABTestApiProxy()
                .Received(1)
                .SetABTestGroupsAsync(
                    "account.a",
                    Arg.Is<ABTestGroupSettingRequest>(x => x != null
                        && x.AssignmentRequests.Count == 1
                        && x.UnassignmentRequests.Count == 3
                        && x.AssignmentRequests.First().ABTestId == "test.1"
                        && x.AssignmentRequests.First().RequestedByAdmin == false
                        && x.AssignmentRequests.First().ABTestGroup == ABTestGroup.A
                        && x.UnassignmentRequests.All(x => new List<string>() { "test.2", "test.3", "test.invalid" }.Contains(x.ABTestId))
                        && x.UnassignmentRequests.All(x => x.RequestedByAdmin == false)),
                    Arg.Any<CancellationToken>());
        }

        [Fact]
        public async Task SetABTestGroup_should_not_call_account_api_if_desc_is_invalid()
        {
            OverrideDescs(OriginType.Bro, GetInvalidABTestDescs().ToArray());
            var service = CreateService<ABTestService.ABTestService>();

            _gear.MockRpc(NameOf.AccountApi());
            _gear.AccountABTestApiProxy().GetABTestGroupsAsync(default, default)
                .ReturnsForAnyArgs(HcTypedResult<List<ABTestGroupAssignment>>.Success(GetABTestGroupAssignments()));

            var dateTime = Substitute.For<IBroDateTime>();
            dateTime.UtcNow.ReturnsForAnyArgs(DateTime.Parse("2099-12-30 11:00:00"));
            BroDateTimeStatic.OpenTestScope(dateTime);

            await service.SetABTestGroupsOnLoginAsync("account.a");

            await _gear.AccountABTestApiProxy()
                .DidNotReceiveWithAnyArgs()
                .SetABTestGroupsAsync(default, default, default);
        }

        [Fact]
        public async Task AssignABTestGroup_should_return_fail_if_test_id_is_invalid()
        {
            OverrideDescs(OriginType.Bro, GetABTestDescs().ToArray());
            var service = CreateService<ABTestService.ABTestService>();

            _gear.MockRpc(NameOf.AccountApi());
            var dateTime = Substitute.For<IBroDateTime>();
            dateTime.UtcNow.ReturnsForAnyArgs(DateTime.Parse("2099-12-30 11:00:00"));
            BroDateTimeStatic.OpenTestScope(dateTime);

            var result = await service.AssignABTestGroupAsync("account.a", "test.invalid");

            Assert.True(result.IsError());
        }

        [Fact]
        public async Task AssignABTestGroup_should_return_fail_if_test_assignment_is_not_in_progress()
        {
            OverrideDescs(OriginType.Bro, GetABTestDescs().ToArray());
            var service = CreateService<ABTestService.ABTestService>();

            _gear.MockRpc(NameOf.AccountApi());
            var dateTime = Substitute.For<IBroDateTime>();
            dateTime.UtcNow.ReturnsForAnyArgs(DateTime.Parse("2099-12-29 11:00:00"));
            BroDateTimeStatic.OpenTestScope(dateTime);

            var result = await service.AssignABTestGroupAsync("account.a", "test.1");

            Assert.True(result.IsError());
        }

        [Fact]
        public async Task AssignABTestGroup_should_return_fail_if_test_AssignGroupOnLogin_is_true()
        {
            OverrideDescs(OriginType.Bro, GetABTestDescs().ToArray());
            var service = CreateService<ABTestService.ABTestService>();

            _gear.MockRpc(NameOf.AccountApi());
            var dateTime = Substitute.For<IBroDateTime>();
            dateTime.UtcNow.ReturnsForAnyArgs(DateTime.Parse("2099-12-30 11:00:00"));
            BroDateTimeStatic.OpenTestScope(dateTime);

            var result = await service.AssignABTestGroupAsync("account.a", "test.1");

            Assert.True(result.IsError());
        }

        [Fact]
        public async Task AssignABTestGroup_should_assign_test_group_if_requested_data_is_valid()
        {
            OverrideDescs(OriginType.Bro, GetExplicitAssignmentABTestDesc().ToArray());
            var service = CreateService<ABTestService.ABTestService>();

            _gear.MockRpc(NameOf.AccountApi());
            var dateTime = Substitute.For<IBroDateTime>();
            dateTime.UtcNow.ReturnsForAnyArgs(DateTime.Parse("2099-12-30 11:00:00"));
            BroDateTimeStatic.OpenTestScope(dateTime);

            var result = await service.AssignABTestGroupAsync("account.a", "test.1");

            Assert.True(result.IsSuccess());
            await _gear.AccountABTestApiProxy()
                .Received(1)
                .SetABTestGroupsAsync(
                    "account.a",
                    Arg.Is<ABTestGroupSettingRequest>(x => x != null
                        && x.AssignmentRequests.Count == 1
                        && x.UnassignmentRequests == null
                        && x.AssignmentRequests.First().ABTestId == "test.1"
                        && x.AssignmentRequests.First().RequestedByAdmin == false
                        && x.AssignmentRequests.First().ABTestGroup == ABTestGroup.A),
                    Arg.Any<CancellationToken>());
        }

        private static IEnumerable<ABTestDesc> GetABTestDescs()
        {
            return new List<ABTestDesc>
            {
                new ABTestDesc
                {
                    Id = "test.1",
                    NumberOfABTestGroup = 1,
                    ABTestStartTimeInUTC = DateTime.Parse("2099-12-31 15:00:00"),
                    ABTestFinishTimeInUTC = DateTime.Parse("2099-12-31 18:00:00"),
                    GroupAssignmentStartTimeInUTC = DateTime.Parse("2099-12-30 10:00:00"),
                    GroupAssignmentFinishTimeInUTC = DateTime.Parse("2099-12-30 12:00:00"),
                    GroupAssignmentRatio = new Dictionary<ABTestGroup, int>
                    {
                        [ABTestGroup.A] = 100,
                    },
                    AssignGroupOnLogin = true,
                },
                new ABTestDesc
                {
                    Id = "test.2",
                    NumberOfABTestGroup = 3,
                    ABTestStartTimeInUTC = DateTime.Parse("2099-11-30 15:00:00"),
                    ABTestFinishTimeInUTC = DateTime.Parse("2099-11-30 18:00:00"),
                    GroupAssignmentStartTimeInUTC = DateTime.Parse("2099-11-29 10:00:00"),
                    GroupAssignmentFinishTimeInUTC = DateTime.Parse("2099-11-29 12:00:00"),
                    GroupAssignmentRatio = new Dictionary<ABTestGroup, int>
                    {
                        [ABTestGroup.A] = 30,
                        [ABTestGroup.B] = 50,
                        [ABTestGroup.C] = 20,
                    },
                    AssignGroupOnLogin = true,
                },
                new ABTestDesc
                {
                    Id = "test.3",
                    NumberOfABTestGroup = 3,
                    ABTestStartTimeInUTC = DateTime.Parse("2099-10-31 15:00:00"),
                    ABTestFinishTimeInUTC = DateTime.Parse("2099-10-31 18:00:00"),
                    GroupAssignmentStartTimeInUTC = DateTime.Parse("2099-10-30 10:00:00"),
                    GroupAssignmentFinishTimeInUTC = DateTime.Parse("2099-10-30 12:00:00"),
                    GroupAssignmentRatio = new Dictionary<ABTestGroup, int>
                    {
                        [ABTestGroup.A] = 30,
                        [ABTestGroup.B] = 50,
                        [ABTestGroup.C] = 20,
                    },
                    AssignGroupOnLogin = true,
                },
            };
        }

        private static IEnumerable<ABTestDesc> GetExplicitAssignmentABTestDesc()
        {
            var testDescs = GetABTestDescs();
            testDescs.ForEach(x => x.AssignGroupOnLogin = false);

            return testDescs;
        }

        private static IEnumerable<ABTestDesc> GetInvalidABTestDescs()
        {
            return new List<ABTestDesc>
            {
                new ABTestDesc
                {
