                    AssignedByAdmin = false,
                },
                new ABTestGroupAssignment
                {
                    ABTestId = "test.3",
                    ABTestGroup = ABTestGroup.C,
                    AssignedByAdmin = false,
                },
                new ABTestGroupAssignment
                {
                    ABTestId = "test.invalid",
                    ABTestGroup = ABTestGroup.D,
                    AssignedByAdmin = false,
                },
            };
        }
    }
