using System;
using System.Linq;
using Amazon.Runtime.Internal.Transform;
using Bro.Common;
using Bro.Common.Configurator;
using Bro.Common.Database;
using Bro.Testing;
using Bro.Testing.Mocking.Gears;
using DryIoc;
using Helical;
using Helical.Database;
using Helical.Game.Common;
using NSubstitute;
using Xunit;

namespace Bro.Services.Test
{
    public class BaseServiceUnitTest : BaseBroUnitTest
    {
        private readonly IContainer _container;

        public BaseServiceUnitTest() : base()
        {
            _container = new Container(scopeContext: new AsyncExecutionFlowScopeContext()).OpenScope();
            _container.RegisterInstance<IHcGear>(_gear, Reuse.InCurrentScope);
            _container.RegisterInstance(_gearOption, Reuse.InCurrentScope);
            _container.RegisterInstance(_dateTime, Reuse.InCurrentScope);
            _container.RegisterInstance(_descSystem, Reuse.InCurrentScope);

            _gear.MockQueueHost(NameOf.UserProxyQueueHostName());
            _gear.UseMockCache(NameOf.PlayerStatusCacheHost());
            Queue<InventoryServiceQueue>(NameOf.InventoryMessageQueueName())?.Clear();
            Queue<BattlePassServiceQueue>(NameOf.BattlePassServiceQueueName())?.Clear();
            Queue<SurvivorPassServiceQueue>(NameOf.SurvivorPassServiceQueueName())?.Clear();
            Queue<EventPassServiceQueue>(NameOf.EventPassServiceQueueName())?.Clear();
            Queue<BattlePassProcessingServiceQueue>(NameOf.BattlePassPlayLogQueueName())?.Clear();
            Queue<OutgameEventQueue>(NameOf.OutgameEventQueueName())?.Clear();
        }

        public MockQueueImplementation ClientApiQueue(string accountId)
        {
            var clientApiQueue = _gear.MockQueue<ClientApiQueue>(NameOf.UserProxyQueueHostName(), NameOf.ClientApiQueueName(accountId));
            return (clientApiQueue.Implementation as MockQueueImplementation);
        }
        public MockQueueImplementation Queue<T>(string queueName = null)
        {
            var queueType = typeof(T);

            var resolved = _container.Resolve<MockQueueImplementation>(ifUnresolved: IfUnresolved.ReturnDefault, serviceKey: queueType);
            if (resolved != null)
            {
                return resolved;
            }

            string name = queueName;
            if (string.IsNullOrEmpty(queueName))
            {
                var queueNameMethod = typeof(NameOf).GetMethods().First(x => x.Name == $"{queueType.Name}Name");
                name = queueNameMethod.Invoke(null, null) as string;
            }

            var queue = typeof(MockGear).GetMethod("MockDesignatedQueue")
                ?.MakeGenericMethod(queueType)
                .Invoke(_gear, new[] { name }) as MockQueueImplementation;

            _container.RegisterInstance(queue, Reuse.InCurrentScope, serviceKey: queueType);
            return queue;
        }

        public IHcDbRepository<T> DbRepo<T>(string repoName = null) where T : IHcDbBaseDocument
        {
            var resolved = _container.Resolve<IHcDbRepository<T>>(IfUnresolved.ReturnDefault);
            if (resolved != null)
            {
                return resolved;
            }

            var docType = typeof(T);

            if (repoName == null)
            {
                var dbnameMethod = typeof(NameOf).GetMethods().FirstOrDefault(x => x.Name == docType.Name.Replace("Document", "Database"));
                if (dbnameMethod == null)
                {
                    throw new Exception($"Cannot find database name for {docType.Name}. Assign database name manually by calling DbRepo<{docType.Name}>(string repoName) .");
                }
                repoName = dbnameMethod.Invoke(null, null) as string;
            }
            var repo = typeof(MockGear).GetMethod("MockRepository")
                ?.MakeGenericMethod(docType)
                .Invoke(_gear, new[] { repoName }) as IHcDbRepository<T>;
            _container.RegisterInstance(typeof(IHcDbRepository<T>), repo, Reuse.InCurrentScope);
            return repo;
        }

        public T CreateService<T>(params object[] additionalArgs) where T : class
        {
            var serviceType = typeof(T);
            var constructor = serviceType.GetConstructors()[0];

            var constructorParams = constructor.GetParameters();
            constructorParams.ForEach(paramInfo =>
            {
                var resolved = _container.Resolve(paramInfo.ParameterType, true);
                if (!paramInfo.ParameterType.IsArray && resolved != null)
                {
                    return;
                }

                var arg = additionalArgs.FirstOrDefault(x => paramInfo.ParameterType.IsInstanceOfType(x));
                if (arg != null)
                {
                    _container.RegisterInstance(paramInfo.ParameterType, arg, Reuse.InCurrentScope);
                    return;
                }

                // ì´ê±° í•˜ëŠ” ì´ìœ  - RegisterDelegateëŠ” open generic typeì— ëŒ€í•´ ì‚¬ìš©í•  ìˆ˜ê°€ ì—†ìŒ.
                if (paramInfo.ParameterType.IsGenericType && paramInfo.ParameterType.GetGenericTypeDefinition() == typeof(IHcDbRepository<>))
                {
                    typeof(BaseServiceUnitTest).GetMethod("DbRepo")
                        ?.MakeGenericMethod(paramInfo.ParameterType.GenericTypeArguments[0]).Invoke(this, new object[] { null });
                }

            });

            _container.Register<T>(Reuse.InCurrentScope, setup: Setup.With(allowDisposableTransient: true));
            return _container.Resolve<T>();
        }

        public T CreateBypassApi<T>(SampleUser user, params object[] additionalArgs) where T : class
        {
            var api = CreateService<T>(additionalArgs);
            var propInfo = typeof(T).GetProperty("InvokeContext");
            if (propInfo == null)
            {
                throw new Exception("InvokeContext property is missing.");
            }

            var invokeContext = new HcInvokeContext
            {
                UserCallData = new HcUserCallData {Roles = user.Roles}
            };
            propInfo.SetValue(api, invokeContext);
            return api;
        }

        public T CreateBypassApi<T>(params object[] additionalArgs) where T : class
        {
            var user = Samples.MakeSampleUser("steam");
            return CreateBypassApi<T>(user, additionalArgs);
        }

        public void RegisterInstance<T>(Type serviceType, T instance) where T : class
        {
            _container.RegisterInstance(serviceType, instance);
        }
    }

    public class BaseBroUnitTest
    {
        protected MockGear _gear;
        protected HcGearOption _gearOption;


        // Instantiating the mock BroDescSystem is expensive and most tests don't use it.
        // Initializing this lazily significantly decreases the run time of the test suite.
        private Lazy<IBroDescSystem> _lazyDescSystem;
        protected IBroDescSystem _descSystem
        {
            get => _lazyDescSystem.Value;
            set => _lazyDescSystem = new Lazy<IBroDescSystem>(value);
        }

        protected readonly IBroDateTime _dateTime = Substitute.For<IBroDateTime>();

        protected UserAccessData CompareUserAccessData(UserAccessData userAccessData)
        {
            return NSubstitute.Arg.Is<UserAccessData>(actual => actual.AccountId == userAccessData.AccountId
                && actual.OriginType == userAccessData.OriginType);
        }

        protected void AssertEqualUserAccessData(UserAccessData expected, UserAccessData actual)
        {
            Assert.Equal(expected.AccountId, actual.AccountId);
            Assert.Equal(expected.OriginType, actual.OriginType);
        }

        protected T[] CompareArray<T>(T[] expected) where T : IComparable<T>, IEquatable<T>
        {
            return NSubstitute.Arg.Is<T[]>(actual => actual.OrderBy(x => x).SequenceEqual(expected.OrderBy(x => x)));
        }

        public BaseBroUnitTest()
        {
            BroConf.OpenTestScope();
            BroDateTimeStatic.OpenTestScope(_dateTime);
            _gearOption = new HcGearOption { };
            _gear = new MockGear();
            _lazyDescSystem = new Lazy<IBroDescSystem>(() => BroDescSystem.Mock(_gear, null, _dateTime));
            _dateTime.UtcNow.ReturnsForAnyArgs(DateTime.UtcNow);

            new BroConfUpdater(_gear).ConfigurationServiceStarted();
            MockQueueImplementation.OpenTestScope();

            BroConf.UpdateCurrentSeasonOption(new CurrentSeasonOption
            {
                CurrentSeasonType = MockRankPointDescDefault.CurrentSeason,
            });
        }

        protected void OverrideDescs<T>(OriginType o, T[] descs) where T : Desc
        {
            var sub = _descSystem.SubSystem(o) as MockBroDescSubSystem;
            sub.OverrideDescs(descs);
        }

        protected void OverrideDesc<T>(OriginType o, T desc) where T : Desc
        {
            var sub = _descSystem.SubSystem(o) as MockBroDescSubSystem;
            sub.OverrideDescs(new T[] { desc });
        }
    }

